import React, { Component } from "react";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Line } from "react-konva";

export default class defconScale extends Component {
  render() {
    return (
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Text text="Defcon Complete" fontSize={25} />
          <Rect
            x={20}
            y={100}
            width={100}
            height={100}
            fill="blue"
            shadowBlur={10}
          />
          <Rect
            x={20}
            y={200}
            width={100}
            height={100}
            fill="green"
            shadowBlur={10}
          />
          <Rect
            x={20}
            y={300}
            width={100}
            height={100}
            fill="yellow"
            shadowBlur={10}
          />
          <Rect
            x={20}
            y={400}
            width={100}
            height={100}
            fill="red"
            shadowBlur={10}
          />
          {/*y is offset from top, x is offset from left side*/}
          <Rect
            x={20}
            y={500}
            width={100}
            height={100}
            fill="white"
            shadowBlur={10}
          />
        </Layer>
      </Stage>
    );
  }
}
